class App extends Component {
  state = {};
  spotify = new SpotifyWebApi();

  setClientTokens() {
    let pathArray = window.location.pathname.split('&');
    let clientInfo  = {};

    pathArray.forEach((item, index) => {
      let split = item.split('=');
      clientInfo[split[0].replace('/', '')] = split[1];
    });
    console.log(clientInfo);
    this.spotify.setAccessToken(clientInfo.access_token);
  }

  onChangeHandler(event) {
    console.log(event.target.value);

    let queryTerm = event.target.value;
    this.spotify.searchArtists(queryTerm, {limit: 20}, (error, data) => {
      if (error) {
        console.log(error);
      } else {
        let artists = [];
        artists = data.artists.items.map((item) => {
          console.log(item);
          return(
            <div key={item.id}>
              <img src={(item.images[2]) ? item.images[2].url: null} />
              <h3 style={{color: 'white'}}>{item.name}</h3>
            </div>
          );
        });
        console.log(artists);
        this.setState({
          artistsInfo: artists
        });
      }
    });
  }


  constructor() {
    super();
    this.setClientTokens()
    console.log(this.spotify.getAccessToken())
    this.onChangeHandler = this.onChangeHandler.bind(this)
    this.queryArtistTracks = this.queryArtistTracks.bind(this)
  }

  queryArtistTracks() {
    this.spotify.getArtistRelatedArtists('5K4W6rqBFWDnAN6FQUkS6x', {limit: 20}, (error, data) => {
      if (error) {
        console.log(error);
      } else {
        data.artists.forEach((item) => {
          console.log(item.name);
        });

      }
    });
  }


  render() {
    return (
    //   <div style={{padding: '30px'}}>
