import React from 'react';
import styled from "@emotion/styled";

const login = (props) => {
    let clientId = '14b4201cd7a14ba09d298bb2b48cc09a'; // Your client id
    let redirectUri = 'http://localhost:3000/'; // Your redirect uri
    let scope = '';
    let state = '123';  

    let url = 'https://accounts.spotify.com/authorize' + 
    '?client_id=' + clientId +
    '&redirect_uri=' + encodeURIComponent(redirectUri) +
    '&scope=' + encodeURIComponent(scope) +
    '&response_type=token' +
    '&states=' + encodeURIComponent(state);

    const Container = styled.div`
        padding: 30px;
        margin: 0 auto;
        max-width: 600px;
    `
    const Title = styled.h1`
        margin-bottom: 20px;
        text-align: center;
    `

    const Button = styled.div`
        width: 200px;
        border: 2px solid black;
        background-color: black;
        color: white;
        text-align: center;
        padding: 10px;
        margin-bottom: 10px;
        box-sizing: border-box;
        margin: 0 auto;
    `

    let child = {}
    if (!window.location.hash) {
        child = <a style={{textDecoration: 'none'}} href={url}><Button>Login To Spotify</Button></a>;
    } else {
        child = <>{props.children}</>;
    }

    return (
        <Container>
            <Title>DiscoverArtists</Title>
            {child}
        </Container>
    );
}

export default login;
