import React, { Component } from "react";
import "./App.css";
import SpotifyWebApi from "spotify-web-api-js";


class App extends Component {
  state = {};
  spotify = new SpotifyWebApi();

  constructor() {
    super();
    this.setClientTokens()
    this.onChangeHandler = this.onChangeHandler.bind(this)
    this.onClickHandler = this.onClickHandler.bind(this)
    this.state = {
      selected: false
    }

  }

  setClientTokens() {
    let pathArray = window.location.pathname.split('&');
    let clientInfo  = {};

    pathArray.forEach((item, index) => {
      let split = item.split('=');
      clientInfo[split[0].replace('/', '')] = split[1];
    });
    this.spotify.setAccessToken(clientInfo.access_token);
  }

  onChangeHandler(event) {
    let queryTerm = event.target.value;
    let artists = [];
    this.spotify.searchArtists(queryTerm, {limit: 20}, (error, data) => {
      if (error) {
        console.log(error);
      } else {
        let artists = [];
        const containerStyle = {
          display: 'flex',
          alignItems: 'center',
          paddingBottom: '10px'
        }

        const imageStyle = {
          width: '70px',
          height: '70px',
          marginRight: '20px',
        }

        const nameStyle = {
          fontSize: '1.2em',
          fontWeight: 'bolder'

        }

        artists = data.artists.items.map((item) => {
          return(
            <div key={item.id} style={containerStyle} onClick={() => {this.onClickHandler(item.id, event)}}>
              <img src={(item.images[2]) ? item.images[2].url: 'https://via.placeholder.com/70'} style={imageStyle}/>
              <div style={nameStyle}>{item.name}</div>
            </div>
          );
        });
        this.setState({
          artistsList: artists
        });
      }
    });
    // console.log(artists)
  }

  onClickHandler(id, event) {
    console.log(id);
    this.spotify.getArtist(id, (error, data) => {
      if (error) {
        console.log(error);
      } else {
        // console.log(data);
      }
    });

    this.spotify.getArtistRelatedArtists(id, (error, data) => {
      if (error) {
        console.log(error);
      } else {
        // console.log(data);
        let relatedArtists = data.artists.map((item, index) => {
          return (<div key={item.id}>
            <h1>{item.name}</h1>
            <img src={(item.images[2]) ? item.images[2].url: 'https://via.placeholder.com/70'} style={{width: '70px', height: '70px'}}/>
          </div>)
        })
        // console.log(relatedArtists)
        this.setState({
          relatedArtists: relatedArtists
        })
      }
    });

    this.setState({
      selected: true,
    })
  }




  // queryArtistTracks() {
  //   this.spotify.getArtistRelatedArtists('5K4W6rqBFWDnAN6FQUkS6x', {limit: 20}, (error, data) => {
  //     if (error) {
  //       console.log(error);
  //     } else {
  //       data.artists.forEach((item) => {
  //         console.log(item.name);
  //       });

  //     }
  //   });
  // }


  render() {
    const containerStyle = {
      padding: '30px'
    };
    const inputStyle = {
      width: '100%',
      border: '2px solid #334',
      padding: '10px',
      boxSizing: 'border-box',
      marginBottom: '30px'
    };
    const titleStyle = {
      marginBottom: '20px'
    }


    if (!this.state.selected) {
        return (
          <div style={containerStyle}>
            <h3 style={titleStyle}>DiscoverArtists</h3>
            <input style={inputStyle} type="text" onChange={this.onChangeHandler} placeholder="Search for Artist"/>
            {this.state.artistsList}
          </div>
        );
      } else {
        console.log(this.state.relatedArtists)
      }      
      return (this.state.relatedArtists);
    }
}

export default App;
