import React from 'react';
import styled from '@emotion/styled';

const login = (props) => {
    var clientId = '14b4201cd7a14ba09d298bb2b48cc09a'; // Your client id
    var redirectUri = 'http://localhost:3000/'; // Your redirect uri
    var scope = '';
    var state = '123';  

    let url = 'https://accounts.spotify.com/authorize' + 
    '?client_id=' + clientId +
    '&redirect_uri=' + encodeURIComponent(redirectUri) +
    '&scope=' + encodeURIComponent(scope) +
    '&response_type=token' +
    '&states=' + encodeURIComponent(state);

    const titleStyle = {
    marginBottom: '20px',
    textAlign: 'center'
    }

    const Container = styled.div`
        padding: 30px;
    `
    const Button = styled.div`
        width: 200px;
        background-color: black;
        color: white;
        text-align: center;
        padding: 10px;
        margin-bottom: 10px;
        box-sizing: border-box;
        margin: 0 auto;
    `

    {}

    if (!window.location.hash) {
        return (
            <Container>
                <h1 style={titleStyle}>DiscoverArtists</h1>
                <a style={{textDecoration: 'none', margin: '0 auto'}} href={url}><Button>Login To Spotify</Button></a>
            </Container>
            );
    } else {
        return (
        <Container>
            <h1 style={titleStyle}>DiscoverArtists</h1>
            {props.children}
        </Container>);
    }    
}

export default login;
