import React, {Component} from 'react';
import styled from "@emotion/styled";

class Info extends Component {
    shouldComponentUpdate
    render() {
        const Button = styled.div`
            border: 2px solid black;
            background-color: black;
            color: white;
            text-align: center;
            padding: 10px;
            margin-bottom: 10px;
            box-sizing: border-box;
        `

        const Text = styled.div`
            margin-top: 20px;
            margin-bottom: 20px;
            font-size: 1.2em;
        `

    return (
        <div>
            <Button onClick={this.props.buttonHandler}>Back to Search</Button>
            <Text>If you like <strong>{this.props.name}</strong></Text>
            <Text>You might like: </Text>
          </div>
        );
    }


}

export default Info;
