class App extends Component {
  state = {};
  spotify = new SpotifyWebApi();

  setClientTokens() {
    let pathArray = window.location.pathname.split('&');
    let clientInfo  = {};

    pathArray.forEach((item, index) => {
      let split = item.split('=');
      clientInfo[split[0].replace('/', '')] = split[1];
    });
    console.log(clientInfo);
    this.spotify.setAccessToken(clientInfo.access_token);
  }

  onChangeHandler(event) {
    console.log(event.target.value);
    if (event.target.value) {
    let queryTerm = event.target.value;
    this.spotify.searchArtists(queryTerm, {limit: 1}, (error, data) => {
      console.log(data.artists.items[0])
      this.setState({
        item1: <h1>{data.artists.items[0].name}</h1>,
        item2: <h1>{data.artists.items[1].name}</h1>,
        item3: <h1>{data.artists.items[2].name}</h1>,
        item4: <h1>{data.artists.items[3].name}</h1>,
        item4: <h1>{data.artists.items[0].name}</h1>
      });
    });
  }
  }


  constructor() {
    super();
    this.setClientTokens()
    console.log(this.spotify.getAccessToken())
    this.onChangeHandler = this.onChangeHandler.bind(this)
  }


  render() {
    return (
      <div>
    <input type="text" onChange={this.onChangeHandler} />
    {this.state.item}
    </div>
    );
  } 
}

export default App;
