import React, { Component } from "react";
import "./App.css";
import SpotifyWebApi from "spotify-web-api-js";


class App extends Component {
  state = {};
  spotify = new SpotifyWebApi();

  constructor() {
    super();
    this.setClientTokens()
    this.onChangeHandler = this.onChangeHandler.bind(this)
    // this.queryArtistTracks = this.queryArtistTracks.bind(this)
  }

  setClientTokens() {
    let pathArray = window.location.pathname.split('&');
    let clientInfo  = {};

    pathArray.forEach((item, index) => {
      let split = item.split('=');
      clientInfo[split[0].replace('/', '')] = split[1];
    });
    this.spotify.setAccessToken(clientInfo.access_token);
  }

  onChangeHandler(event) {
    let queryTerm = event.target.value;
    let artists = [];
    this.spotify.searchArtists(queryTerm, {limit: 20}, (error, data) => {
      if (error) {
        console.log(error);
      } else {
        let artists = [];
        const imageStyle = {
          width: '70px',
          height: '70px'
        }






        artists = data.artists.items.map((item) => {
          return(
            <div key={item.id}>
              <img src={(item.images[2]) ? item.images[2].url: 'null'} />
              <h3>{item.name}</h3>
            </div>
          );
        });
        this.setState({
          artistsList: artists
        });
      }
    });
    console.log(artists)
  }




  // queryArtistTracks() {
  //   this.spotify.getArtistRelatedArtists('5K4W6rqBFWDnAN6FQUkS6x', {limit: 20}, (error, data) => {
  //     if (error) {
  //       console.log(error);
  //     } else {
  //       data.artists.forEach((item) => {
  //         console.log(item.name);
  //       });

  //     }
  //   });
  // }


  render() {
    const containerStyle = {
      padding: '30px'
    };
    const inputStyle = {
      width: '100%',
      border: '2px solid #334',
      padding: '10px',
      boxSizing: 'border-box',
      marginBottom: '30px'
    };
    const titleStyle = {
      marginBottom: '20px'
    }



    return (
      <div style={containerStyle}>
        <h3 style={titleStyle}>DiscoverArtists</h3>
        <input style={inputStyle} type="text" onChange={this.onChangeHandler} placeholder="Search for Artist"/>

        {this.state.artistsList}
     </div>
    );
  } 
}

export default App;
