import React from 'react';

const login = (props) => {
    var generateRandomString = function(length) {
        var text = '';
        var possible = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';

        for (var i = 0; i < length; i++) {
          text += possible.charAt(Math.floor(Math.random() * possible.length));
        }
        return text;
      };




    var clientId = '14b4201cd7a14ba09d298bb2b48cc09a'; // Your client id
    var clientSecret = 'e331361267864c2a840a29e5f34def8d'; // Your secret
    var redirectUri = 'https://localhost:3000/index.html'; // Your redirect uri
    var scope = 'user-read-private user-read-email';
    var state = generateRandomString(16);  

    let url = 'https://accounts.spotify.com/authorize' + 
    '?client_id=' + clientId +
    '&redirect_uri=' + encodeURIComponent(redirectUri) +
    '&scope=' + encodeURIComponent(scope) +
    '&response_type=token' +
    '&states=' + encodeURIComponent(state);

    return <a href={url}>Hello From Login</a>
}

export default login;
