import React, { Component } from "react";
import "./App.css";
import SpotifyWebApi from "spotify-web-api-js";

class App extends Component {
  state = {}
  constructor() {
    let pathArray = window.location.pathname.split('&');
    let clien_tokens = {}

    pathArray.forEach((item, index) => {
      var split = item.split('=');
      obj[split[0].replace('/', '')] = split[1];
    });

    this.spotify = new SpotifyWebApi();
    this.spotify.setAccessToken(obj.access_token)


  }


  render() {

    var arr = window.location.pathname.split('&');
    var obj = {};

    arr.forEach((item, index) => {
      var split = item.split('=');
      obj[split[0].replace('/', '')] = split[1];
    });

    var spotify = new SpotifyWebApi();
    spotify.setAccessToken(obj.access_token)



    spotify.searchArtists('nav', (err, data) => {
      if (err) {
        console.error(err);
      } else {
        console.log(data.artists.items[0].id);
          spotify.getArtistAlbums(data.artists.items[0].id, (err, data) => {
            if (err) {
              console.error(err);
            } else {
              this.setState({
                name: data.items.map((item, index) => {
                  return <h1 key={index}>{item.name}</h1>
                })
              });
            }
          });
      }
    });


    return (
      <div className="App">
        <a href="http://localhost:8888"><button>Login to spotify</button></a>  
        {this.state.name}      
      </div>
    );
  }
}

export default App;
