import React from 'react';

const info = ({name, buttonHandler}) => {
    const buttonStyle = {
        width: '100px',
        height: '25px',
        border: '2px solid black',
        backgroundColor: 'black',
        color: 'white',
        fontSize: '1.2em',
        textAlign: 'center',
        padding: '5px',
        marginBottom: '10px'
    }




    return (
    <div>
        <div style={buttonStyle} onClick={buttonHandler}>Back</div>
        <div>If you like {name}</div>
        <div>You might like: </div>
      </div>
    );
}

export default info;
