import React from 'react';
import styled from "@emotion/styled";

const info = ({name, buttonHandler}) => {
    const Button = styled.div`
        width: 100px;
        border: 2px solid black;
        background-color: black;
        color: white;
        text-align: center;
        padding: 10px;
        margin-bottom: 10px;
        box-sizing: border-box;
    `

    const Text = styled.div`
        margin: '20px 0'
    `


    return (
    <div>
        <Button onClick={buttonHandler}>Back to Search</Button>
        <Text>If you like <strong>{name}</strong></Text>
        <Text>You might like: </Text>
      </div>
    );
}

export default info;
