import React from 'react';
import styled from '@emotion/styled';

const login = (props) => {
    var clientId = '14b4201cd7a14ba09d298bb2b48cc09a'; // Your client id
    var redirectUri = 'https://dazzling-cori-0d0d03.netlify.com/'; // Your redirect uri
    var scope = '';
    var state = '123';  

    let url = 'https://accounts.spotify.com/authorize' + 
    '?client_id=' + clientId +
    '&redirect_uri=' + encodeURIComponent(redirectUri) +
    '&scope=' + encodeURIComponent(scope) +
    '&response_type=token' +
    '&states=' + encodeURIComponent(state);

    const titleStyle = {
    marginBottom: '20px'
    }
    const buttonStyle = {
    width: '200px',
    border: '2px solid black',
    backgroundColor: 'black',
    color: 'white',
    textAlign: 'center',
    padding: '10px',
    marginBottom: '10px',
    boxSizing: 'border-box'
    }

    const linkStyle = {
        textDecoration: 'none'
    }

    const Container = styled.div`
    padding: 30px;
    `




    if (!window.location.hash) {
        return (
            <Container>
                <h3 style={titleStyle}>DiscoverArtists</h3>
                <a style={linkStyle} href={url}><div style={buttonStyle}>Login To Spotify</div></a>
            </Container>
            );
    } else {
        return (
        <Container>
            <h3 style={titleStyle}>DiscoverArtists</h3>
            {props.children}
        </Container>);
    }    
}

export default login;
