import React from 'react';
import styled from "@emotion/styled";

const search = (props) => {

  const Input = styled.input`
    width: 100%;
    border: 2px solid #334;
    padding: 10px;
    box-sizing: border-box;
    margin-bottom: 30px;
    font-size: 1.3em;
    `

    return (
      <div>
        <Input type="text" onChange={props.onChangeHandler} placeholder="Search for Artist"/>
      </div>
    );
}

export default search;
