import React, { Component } from "react";
import "./App.css";
import SpotifyWebApi from "spotify-web-api-js";

class App extends Component {

  constructor() {
    super()
    var arr = window.location.pathname.split('&');
    var obj = {};

    arr.forEach((item, index) => {
      var split = item.split('=');
      obj[split[0].replace('/', '')] = split[1];
    });

    var spotify = new SpotifyWebApi();
    spotify.setAccessToken(obj.access_token)
    let albumNames = [];

    spotify.getArtistAlbums('5K4W6rqBFWDnAN6FQUkS6x', (err, data) => {
      if (err) {
        console.error(err);
      } else {
        albumNames = data.items.map((item, index) => {
          return item.name
        });
      }
    });
  }

  render() {
    return (
      <div className="App">
        <a href="http://localhost:8888"><button>Login to spotify</button></a>        
      </div>
    );
  }
}

export default App;
