import React from "react";

const artist = ({id, name, image, onClickHandler}) => {
  //   return(<div key={item.id} style={containerStyle} onClick={() => {this.onClickHandler(item.id, event)}}>
  //   <img src={(item.images[2]) ? item.images[2].url: 'https://via.placeholder.com/70'} style={imageStyle}/>
  //   <div style={nameStyle}>{item.name}</div>
  // </div>);
  const containerStyle = {
    display: 'flex',
    alignItems: 'center',
    paddingBottom: '10px'
  }

  const imageStyle = {
    width: '70px',
    height: '70px',
    marginRight: '20px',
  }

  const nameStyle = {
    fontSize: '1.2em',
    fontWeight: 'bolder'
  }
  return (
    <div key={id} style={containerStyle}>
      //   <img src={(item.images[2]) ? item.images[2].url: 'https://via.placeholder.com/70'} style={imageStyle}/>
      <div style={nameStyle}>{name}</div>
    </div>
  );
}

export default artist;
