import React from "react";
import icon from "./spotify-icon.png";

const artist = ({name, image, link, onClickHandler}) => {
  const MainContainer = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
  `
  const InfoContainer = styled.div`
    display: flex;
    align-items: center;
    padding-bottom: 10px;
    cursor: pointer;
  `

  const Image = styled.img`
    width: 70px;
    height: 70px;
    margin-right: 20px;
  `

  const Name = styled.div`
    font-size: 1.2em;
    font-weight: bolder;
  `

  const Icon = styled.img`
    width: 40p
  `

  return (
    <MainContainer>
      <InfoContainer onClick={onClickHandler}>
        <Image src={image} />
        <Name>{name}</Name>
      </InfoContainer>
      <div>
      <a href={link}><img src={icon} style={{width: '40px', height: '40px'}}/></a>
      </div>


    </MainContainer>
  );
}

export default artist;
