import React from 'react';
import styled from "@emotion/styled";

const login = (props) => {
    let clientId = '14b4201cd7a14ba09d298bb2b48cc09a'; // Your client id
    let redirectUri = 'http://localhost:3000/'; // Your redirect uri
    let scope = '';
    let state = '123';  

    let url = 'https://accounts.spotify.com/authorize' + 
    '?client_id=' + clientId +
    '&redirect_uri=' + encodeURIComponent(redirectUri) +
    '&scope=' + encodeURIComponent(scope) +
    '&response_type=token' +
    '&states=' + encodeURIComponent(state);

    const Container = styled.div`
        padding: '30px',
        margin: '0 auto',
        maxWidth: '600px'
    `
      const titleStyle = {
        marginBottom: '20px',
        textAlign: 'center'
      }
      const buttonStyle = {
        width: '200px',
        border: '2px solid black',
        backgroundColor: 'black',
        color: 'white',
        textAlign: 'center',
        padding: '10px',
        marginBottom: '10px',
        boxSizing: 'border-box',
        margin: '0 auto'
      }

      const linkStyle = {
          textDecoration: 'none'
      }
      let child = {}
    if (!window.location.hash) {
        child = <a style={linkStyle} href={url}><div style={buttonStyle}>Login To Spotify</div></a>;
    } else {
        child = <>{props.children}</>;
    }

    return (
        <div style={containerStyle}>
            <h1 style={titleStyle}>DiscoverArtists</h1>
            {child}
        </div>
    );
}

export default login;
