class App extends Component {
  state = {};
  spotify = new SpotifyWebApi();

  setClientTokens() {
    let pathArray = window.location.pathname.split('&');
    let clientInfo  = {};

    pathArray.forEach((item, index) => {
      let split = item.split('=');
      clientInfo[split[0].replace('/', '')] = split[1];
    });
    console.log(clientInfo);
    this.spotify.setAccessToken(clientInfo.access_token);
  }

  onChangeHandler(event) {
    console.log(event.target.value);

    let queryTerm = 't';
    this.spotify.searchArtists(queryTerm, {limit: 20}, (error, data) => {
      if (error) {
        console.log(error);
      } else {
        let artists = [];
        // console.log(data.artists.items)
        artists = data.artists.items.map((item) => {
          console.log(item);
          return(
            <div key={item.id} style={{
              display: 'flex',
              maxWidth: '310px',
              backgroundColor: '#333',
              padding: '10px',
              textAlign: 'center',
              boxSizing: 'border-box',
              justifyContent: 'left',
              alignItems: 'center',
              margin: '0 auto'
            }}>
              <img 
                src={(item.images[2]) ? item.images[2].url: null} 
                style={{
                  width: '70px', 
                  height: '70px',
                  marginRight: '10px'
                }} 
                  />
              <h3 
                style={{
                  color: 'white',
                }}>{item.name}</h3>
            </div>
          );
        });
        console.log(artists);
        this.setState({
          artistsInfo: artists
        });
      }
    });
  }


  constructor() {
    super();
    this.setClientTokens()
    console.log(this.spotify.getAccessToken())
    this.onChangeHandler = this.onChangeHandler.bind(this)
  }


  render() {
    return (
      <div style={{}}>
    <h3 style={{color: 'white'}}></h3>
    <input type="text" onChange={this.onChangeHandler} 
      style={{
        width: '100%',
        boxSizing: 'border-box',
        border: '2px solid #ccc',
        borderRadius: '4px',
        fontSize: '16px',
        height: '30px',
        padding: '10px'
      }}
      placeholder="Search for Artist"
        />
    {this.state.artistsInfo}
    </div>
    );
  } 
}

export default App;
