import React, { Component } from 'react';

class Search extends Component {

    render() {
      const inputStyle = {
        width: '100%',
        border: '2px solid #334',
        padding: '10px',
        boxSizing: 'border-box',
        marginBottom: '30px',
        fontSize: '1.3em',
      };


      return <input autoFocus style={inputStyle} type="text" onChange={this.props.onChangeHandler} placeholder="Search for Artist"/>;
    } 
}

export default Search;
