import React, { Component } from "react";
import "./App.css";
import SpotifyWebApi from "spotify-web-api-js";

class App extends Component {

  constructor() {
    this.state = {
      array: []
    }
  }

componentDidMount() {
  var arr = window.location.pathname.split('&');
  var obj = {};

  arr.forEach((item, index) => {
    var split = item.split('=');
    obj[split[0].replace('/', '')] = split[1];
  });

  var spotify = new SpotifyWebApi();
  spotify.setAccessToken(obj.access_token)

  spotify.getArtistAlbums('5K4W6rqBFWDnAN6FQUkS6x')
  .then(function(data) {
    let albumNames = [];
    data.items.forEach((item) => {
      console.log(item.name);
      albumNames.push(<h1>{item.name}</h1>);
    });
    this.setState({
      array: albumNames
    });

  }, function(err) {
    console.error(err);
  });
}

  render() {
    return (
      <div className="App">
        <a href="http://localhost:8888"><button>Login to spotify</button></a> 
        {this.state.array}       
      </div>
    );
  }
}

export default App;
