import React, { Component } from "react";
import "./App.css";
import SpotifyWebApi from "spotify-web-api-js";
import Login from './Login';
import Artist from './Artist';
import Search from './Search';
import Info from './Info';


class App extends Component {
  state = {};
  spotify = new SpotifyWebApi();

  constructor() {
    super();
    this.setClientTokens()
    this.onChangeHandler = this.onChangeHandler.bind(this)
    this.onClickHandler = this.onClickHandler.bind(this)
    this.backButtonHandler = this.backButtonHandler.bind(this)
    this.textInput = React.createRef();
    this.state = {selected: false}    
  }

  focusTextInput() {
    // Explicitly focus the text input using the raw DOM API
    // Note: we're accessing "current" to get the DOM node
    this.textInput.current.focus();
  }

  componentDidMount() {

  }

  setClientTokens() {
    let pathArray = window.location.hash.split('&');
    let clientInfo  = {};
    pathArray.forEach((item, index) => {
      let split = item.split('=');
      clientInfo[split[0].replace('#', '')] = split[1];
    });
      this.spotify.setAccessToken(clientInfo.access_token);
  }

  onChangeHandler(event) {
    console.log(event.target.value);
    let query = event.target.value;
    if (query.length > 0) {
      this.setState({...this.state, artistList: []});
      return;
    }

    // this.spotify.searchArtists(query, {limit: 20}, (error, data) => {
    //   let artists = data.artists.items.map((item) => {
    //     return <Artist 
    //       key={item.id}
    //       name={item.name}
    //       image={(item.images[2]) ? item.images[2].url: 'https://via.placeholder.com/70'}
    //       onClickHandler={() => this.onClickHandler(item.id, item.name, event)}
    //       link={item.uri}
    //     />
    //   });
    //   this.setState({...this.state, artistList: artists});
    // });

  }

  backButtonHandler(event) {
    this.setState({
      ...this.state,
      selected: false,
      artistList: ''
    });
  }


  onClickHandler(id, name, event) {
    console.log(name);
    this.setState({
      ...this.state,
      selected: true,
      name: name,
      artistList: ''
    });

    this.spotify.getArtistRelatedArtists(id, (error, data) => {
      if (error) console.log(error);
      else {
        let relatedArtists = data.artists.map((item) => {
          return <Artist 
            key={item.id}
            name={item.name}
            image={(item.images[2]) ? item.images[2].url: 'https://via.placeholder.com/70'}
            onClickHandler={() => this.onClickHandler(item.id, item.name, event)}
            link={item.uri}
          />
        });
        this.setState({
          ...this.state,
          artistList: relatedArtists
        });
      }
    });
  }


  render() {
    return (
      <Login>
        {!this.state.selected ? 
          <Search autoFocus onChangeHandler={this.onChangeHandler} ref={this.textInput}/>: 
          <Info name={this.state.name} buttonHandler={this.backButtonHandler}/>
        }
        {this.state.artistList}
      </Login>
    );
  }
}

export default App;
