import React from "react";
import icon from "./spotify-icon.png";

const artist = ({name, image, link, onClickHandler}) => {
  const MainContainer = styled.div`
    display: flex;
    justify-content: space-between,
    align-items: center
  `
  const InfoContainer = styled.div`
    display: flex;
    align-items: center;
    padding-bottom: 10px
  `

  const Image = styled.img`
    width: 70px;
    height: 70px;
    margin-right: 20px;
  `

  const Name = styled.div`
    font-size: 1.2em;
    font-weight: bolder;

  `



  const mainContainer = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center'
  }

  const infoContainerStyle = {
    display: 'flex',
    alignItems: 'center',
    paddingBottom: '10px',
  }

  const imageStyle = {
    width: '70px',
    height: '70px',
    marginRight: '20px',
  }

  const nameStyle = {
    fontSize: '1.2em',
    fontWeight: 'bolder'
  }
  return (
    <div style={mainContainer}>
      <div style={infoContainerStyle} onClick={onClickHandler}>
      <img src={image} style={imageStyle}/>
        <div style={nameStyle}>{name}</div>
      </div>
      <div>
      <a href={link}><img src={icon} style={{width: '40px', height: '40px'}}/></a>
      </div>


    </div>
  );
}

export default artist;
